
jQuery(document).ready(function($){$(".fancybox").fancybox();$("#masthead").sticky({topSpacing:0});});jQuery(document).ready(function($){$("ul.dropdown-menu").closest('li').children('a').append("<span class=\"submenu-arrow fa fa-chevron-down\" />");$('.navbar-nav li .submenu-arrow').click(function(e){e.preventDefault();if($(this).hasClass('fa-chevron-down')){$(this).parent().siblings('ul.dropdown-menu').stop(true,true).slideDown(300);$(this).addClass('fa-chevron-up').removeClass('fa-chevron-down');}else{$(this).parent().siblings('ul.dropdown-menu').stop(true,true).slideUp(300);$(this).addClass('fa-chevron-down').removeClass('fa-chevron-up');}});$('.navbar-nav li .submenu-arrow').mouseout(function(){$(this).addClass('fa-chevron-down').removeClass('fa-chevron-up');});});